mapper.select:



		AND menu_key=#{menuKey}


		AND menu_name like concat('%', #{menuName}, '%')


		AND visible = #{visible}


	${params.dataScope}

ORDER BY order_num

mapper.update:
UPDATE sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, menu_type = #{menuType}, target = #{target}, menu_key = #{menuKey}, component = #{component}, visible = #{visible}, perms = #{perms}, icon = #{icon}, path = #{path}, redirect = #{redirect}, hidden_children = #{hiddenChildren}, hidden_header = #{hiddenHeader}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
WHERE menu_id = #{menuId}
